import React, { useState } from 'react';
import styled from 'styled-components';
import { teachersAPI } from '../services/api';

// ... (keep all your existing styled components)

const ProfileUpload = ({ onClose, onTeacherAdded }) => {
  const [preview, setPreview] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    phone: '',
    avatar: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
        setFormData({
          ...formData,
          avatar: reader.result
        });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Create teacher data
      const teacherData = {
        name: formData.name,
        email: formData.email,
        subject: formData.subject,
        phone: formData.phone,
        avatar: formData.avatar || 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-4.0.3&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80',
        performance: Math.floor(Math.random() * 20) + 80, // Random performance 80-100
        students: Math.floor(Math.random() * 20) + 20, // Random students 20-40
        years: Math.floor(Math.random() * 10) + 1 // Random years 1-10
      };

      const response = await teachersAPI.create(teacherData);

      // Notify parent component
      if (onTeacherAdded) {
        onTeacherAdded(response.data);
      }

      onClose();
    } catch (error) {
      console.error('Error creating teacher:', error);
      setError(error.response?.data?.message || 'Failed to create teacher. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    setFormData({
      name: '',
      email: '',
      subject: '',
      phone: '',
      avatar: ''
    });
    setPreview(null);
    onClose();
  };

  return (
    <ModalOverlay>
      <ModalContent>
        <ModalTitle>Add New Teacher</ModalTitle>

        {error && (
          <div style={{
            background: '#fee',
            color: '#c53030',
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            border: '1px solid #feb2b2'
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <UploadArea onClick={() => document.getElementById('file-input').click()}>
            <input
              id="file-input"
              type="file"
              accept="image/*"
              style={{ display: 'none' }}
              onChange={handleImageChange}
            />

            {preview ? (
              <PreviewImage src={preview} alt="Preview" />
            ) : (
              <>
                <UploadIcon>📷</UploadIcon>
                <UploadText>Click to upload profile picture</UploadText>
                <UploadSubtext>JPG, PNG or GIF - Max 5MB</UploadSubtext>
              </>
            )}
          </UploadArea>

          <FormGroup>
            <label className="form-label">Full Name *</label>
            <input
              type="text"
              className="form-input"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </FormGroup>

          <FormGroup>
            <label className="form-label">Email *</label>
            <input
              type="email"
              className="form-input"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </FormGroup>

          <FormGroup>
            <label className="form-label">Subject *</label>
            <input
              type="text"
              className="form-input"
              name="subject"
              value={formData.subject}
              onChange={handleInputChange}
              required
            />
          </FormGroup>

          <FormGroup>
            <label className="form-label">Phone</label>
            <input
              type="tel"
              className="form-input"
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
            />
          </FormGroup>

          <FormActions>
            <CancelButton type="button" onClick={handleCancel} disabled={loading}>
              Cancel
            </CancelButton>
            <PrimaryButton type="submit" disabled={loading}>
              {loading ? 'Adding Teacher...' : 'Add Teacher'}
            </PrimaryButton>
          </FormActions>
        </form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default ProfileUpload;
