import React, { useState } from 'react';
import styled from 'styled-components';
import { Container } from '../styles/globalStyles';
import { authAPI } from '../services/api';

const LoginContainer = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, ${props => props.theme.primary}20, ${props => props.theme.secondary}20);
  padding: 20px;
`;

const LoginCard = styled.div`
  background: ${props => props.theme.cardBg};
  border-radius: 16px;
  padding: 40px;
  box-shadow: 0 20px 40px ${props => props.theme.shadow};
  width: 100%;
  max-width: 450px;
  border: 1px solid ${props => props.theme.border};
  animation: fadeIn 0.8s ease;
`;

const LoginTitle = styled.h1`
  text-align: center;
  color: ${props => props.theme.primary};
  margin-bottom: 30px;
  font-size: 28px;
  font-weight: 700;
`;

const LoginForm = styled.form`
  display: flex;
  flex-direction: column;
`;

const LoginButton = styled.button`
  background: linear-gradient(45deg, ${props => props.theme.primary}, ${props => props.theme.accent});
  color: white;
  border: none;
  border-radius: 8px;
  padding: 14px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 10px;

  &:hover {
    opacity: 0.9;
    transform: translateY(-2px);
    box-shadow: 0 8px 20px ${props => props.theme.primary}40;
  }
`;

const DemoNote = styled.p`
  text-align: center;
  margin-top: 20px;
  color: ${props => props.theme.text};
  opacity: 0.7;
  font-size: 14px;
`;

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMsg, setErrorMsg] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMsg('');

    try {
      // Call backend login API
      const response = await authAPI.login({ email, password });
      const { token, user } = response.data;

      // Save JWT token to localStorage
      localStorage.setItem('token', token);

      // Pass logged-in user to parent (App.js)
      onLogin(user, token);
    } catch (error) {
      console.error(error);
      setErrorMsg(error.response?.data?.message || 'Login failed');
    }
  };

  return (
    <LoginContainer>
      <Container>
        <LoginCard>
          <LoginTitle>EduTrack Login</LoginTitle>
          <LoginForm onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Email</label>
              <input
                type="email"
                className="form-input"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">Password</label>
              <input
                type="password"
                className="form-input"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
              />
            </div>
            {errorMsg && (
              <p style={{ color: '#ef4444', fontSize: '14px', marginTop: '8px' }}>
                {errorMsg}
              </p>
            )}
            <LoginButton type="submit">Sign In</LoginButton>
          </LoginForm>
          <DemoNote>
            Demo: Use admin@edutrack.com / admin123 or nisal@edutrack.com / nisal123
          </DemoNote>
        </LoginCard>
      </Container>
    </LoginContainer>
  );
};

export default Login;
