import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { Container, Grid } from '../styles/globalStyles';
import StatsCard from '../components/StatsCard';
import PerformanceChart from '../components/PerformanceChart';
import RecentActivity from '../components/RecentActivity';
import AIAssistant from '../components/AIAssistant';

const DashboardContainer = styled.div`
  padding: 20px;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: 700;
`;

const TwoColumnGrid = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 20px;

  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
  }
`;

const WelcomeMessage = styled.div`
  background: ${props => props.theme.cardBg};
  border-radius: 12px;
  padding: 30px;
  box-shadow: 0 4px 20px ${props => props.theme.shadow};
  border: 1px solid ${props => props.theme.border};
  text-align: center;
  margin-bottom: 30px;
`;

const Dashboard = ({ user }) => {
  const navigate = useNavigate();

  // Check if user is admin
  const isAdmin = user && user.role === 'admin';

  const statsData = [
    { title: 'Total Teachers', value: '42', change: 5, icon: '👨‍🏫', color: '#6366f1' },
    { title: 'Avg. Performance', value: '87%', change: 2, icon: '📈', color: '#10b981' },
    { title: 'Pending Reviews', value: '12', change: -3, icon: '⏳', color: '#f59e0b' },
    { title: 'Training Completed', value: '28', change: 8, icon: '🎓', color: '#8b5cf6' },
  ];

  if (!isAdmin) {
    return (
      <DashboardContainer>
        <Container>
          <WelcomeMessage>
            <h2>Welcome to EduTrack, {user?.name}!</h2>
            <p>You are logged in as a regular user. Contact your administrator for access to additional features.</p>
            <div style={{ marginTop: '20px', padding: '20px', background: '#f5f5f5', borderRadius: '8px' }}>
              <h3>Available Features:</h3>
              <ul style={{ textAlign: 'left', maxWidth: '400px', margin: '0 auto' }}>
                <li>View your profile information</li>
                <li>Update your personal details</li>
                <li>View your activity history</li>
              </ul>
            </div>
          </WelcomeMessage>
        </Container>
      </DashboardContainer>
    );
  }

  return (
    <DashboardContainer>
      <Container>
        <SectionTitle>Dashboard Overview</SectionTitle>

        <Grid>
          {statsData.map((stat, index) => (
            <StatsCard
              key={index}
              title={stat.title}
              value={stat.value}
              change={stat.change}
              icon={stat.icon}
              color={stat.color}
            />
          ))}
        </Grid>

        <TwoColumnGrid>
          <PerformanceChart />
          <RecentActivity />
        </TwoColumnGrid>

        <AIAssistant />
      </Container>
    </DashboardContainer>
  );
};

export default Dashboard;
