import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { Container } from '../styles/globalStyles';
import { authAPI } from '../services/api';

const LoginContainer = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
`;

const LoginCard = styled.div`
  background: white;
  border-radius: 16px;
  padding: 40px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
  width: 100%;
  max-width: 450px;
`;

const LoginTitle = styled.h1`
  text-align: center;
  color: #6200ee;
  margin-bottom: 30px;
  font-size: 28px;
  font-weight: 700;
`;

const LoginForm = styled.form`
  display: flex;
  flex-direction: column;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
`;

const FormLabel = styled.label`
  display: block;
  margin-bottom: 8px;
  font-weight: 500;
  color: #333;
`;

const FormInput = styled.input`
  width: 100%;
  padding: 12px;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  background: #fff;
  color: #333;
  font-size: 16px;

  &:focus {
    outline: none;
    border-color: #6200ee;
    box-shadow: 0 0 0 2px rgba(98, 0, 238, 0.2);
  }
`;

const LoginButton = styled.button`
  background: linear-gradient(45deg, #6200ee, #ff4081);
  color: white;
  border: none;
  border-radius: 8px;
  padding: 14px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 10px;

  &:hover {
    opacity: 0.9;
    transform: translateY(-2px);
    box-shadow: 0 8px 20px rgba(98, 0, 238, 0.4);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const ErrorMessage = styled.div`
  background: #fee;
  color: #c53030;
  padding: 12px;
  border-radius: 8px;
  border: 1px solid #feb2b2;
  margin-bottom: 20px;
  text-align: center;
`;

const DemoCredentials = styled.div`
  margin-top: 20px;
  padding: 15px;
  background: #f7f7f7;
  border-radius: 8px;
  border: 1px solid #e0e0e0;
`;

const CredentialTitle = styled.h4`
  margin-bottom: 10px;
  color: #333;
`;

const CredentialItem = styled.div`
  margin-bottom: 5px;
  font-size: 14px;
  color: #666;
`;

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await authAPI.login({ email, password });

      if (response.data.success) {
        onLogin(response.data.user, response.data.token);
        navigate('/');
      } else {
        setError(response.data.message || 'Login failed');
      }
    } catch (error) {
      console.error('Login error:', error);
      setError(error.response?.data?.message || 'Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const fillAdminCredentials = () => {
    setEmail('admin@edutrack.com');
    setPassword('admin123');
    setError('');
  };

  const fillNisalCredentials = () => {
    setEmail('nisal@edutrack.com');
    setPassword('nisal123');
    setError('');
  };

  const fillUserCredentials = () => {
    setEmail('user@example.com');
    setPassword('password123');
    setError('');
  };

  return (
    <LoginContainer>
      <Container>
        <LoginCard>
          <LoginTitle>EduTrack Login</LoginTitle>

          {error && <ErrorMessage>{error}</ErrorMessage>}

          <LoginForm onSubmit={handleSubmit}>
            <FormGroup>
              <FormLabel>Email</FormLabel>
              <FormInput
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </FormGroup>

            <FormGroup>
              <FormLabel>Password</FormLabel>
              <FormInput
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
              />
            </FormGroup>

            <LoginButton type="submit" disabled={loading}>
              {loading ? 'Signing in...' : 'Sign In'}
            </LoginButton>
          </LoginForm>

          <DemoCredentials>
            <CredentialTitle>Demo Credentials:</CredentialTitle>
            <CredentialItem>
              <strong>Admin:</strong> admin@edutrack.com / admin123
            </CredentialItem>
            <CredentialItem>
              <strong>Nisal:</strong> nisal@edutrack.com / nisal123
            </CredentialItem>
            <CredentialItem>
              <strong>Regular User:</strong> user@example.com / password123
            </CredentialItem>

            <div style={{ display: 'flex', gap: '10px', marginTop: '15px', flexWrap: 'wrap' }}>
              <button 
                onClick={fillAdminCredentials}
                style={{
                  padding: '8px 12px',
                  border: '1px solid #6200ee',
                  borderRadius: '6px',
                  background: 'transparent',
                  color: '#6200ee',
                  cursor: 'pointer',
                  fontSize: '12px'
                }}
              >
                Use Admin
              </button>
              <button 
                onClick={fillNisalCredentials}
                style={{
                  padding: '8px 12px',
                  border: '1px solid #6200ee',
                  borderRadius: '6px',
                  background: 'transparent',
                  color: '#6200ee',
                  cursor: 'pointer',
                  fontSize: '12px'
                }}
              >
                Use Nisal
              </button>
              <button 
                onClick={fillUserCredentials}
                style={{
                  padding: '8px 12px',
                  border: '1px solid #6200ee',
                  borderRadius: '6px',
                  background: 'transparent',
                  color: '#6200ee',
                  cursor: 'pointer',
                  fontSize: '12px'
                }}
              >
                Use User
              </button>
            </div>
          </DemoCredentials>
        </LoginCard>
      </Container>
    </LoginContainer>
  );
};

export default Login;
