import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { Container } from '../styles/globalStyles';
import { feedbackAPI } from '../services/api';

const FeedbackContainer = styled.div`
  padding: 20px;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: 700;
`;

const AddFeedbackButton = styled.button`
  background: linear-gradient(45deg, ${props => props.theme.primary}, ${props => props.theme.accent});
  color: white;
  border: none;
  border-radius: 8px;
  padding: 12px 20px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 20px;
  transition: all 0.3s ease;

  &:hover {
    opacity: 0.9;
    transform: translateY(-2px);
    box-shadow: 0 8px 20px ${props => props.theme.primary}40;
  }
`;

// ... (keep all other styled components from previous Feedback.js)

const AccessDenied = styled.div`
  text-align: center;
  padding: 60px 20px;
  background: ${props => props.theme.cardBg};
  border-radius: 12px;
  border: 1px solid ${props => props.theme.border};
`;

const Feedback = ({ user }) => {
  const [showForm, setShowForm] = useState(false);
  const [feedbacks, setFeedbacks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [newFeedback, setNewFeedback] = useState({
    student: '',
    course: '',
    rating: 0,
    text: '',
    teacherId: '654d4b5e8a1b2c3d4e5f6a7b'
  });
  const [tempRating, setTempRating] = useState(0);
  const navigate = useNavigate();

  // Check if user is admin
  const isAdmin = user && user.role === 'admin';

  const teachers = [
    { id: '654d4b5e8a1b2c3d4e5f6a7b', name: 'Sarah Johnson', subject: 'Mathematics' },
    { id: '654d4b5e8a1b2c3d4e5f6a7c', name: 'Michael Chen', subject: 'Science' },
    { id: '654d4b5e8a1b2c3d4e5f6a7d', name: 'Emily Rodriguez', subject: 'English' }
  ];

  const courses = [
    'Mathematics 101',
    'Science Fundamentals',
    'English Literature',
    'World History',
    'Art Appreciation'
  ];

  // Load feedbacks from backend
  useEffect(() => {
    loadFeedbacks();
  }, []);

  const loadFeedbacks = async () => {
    try {
      setLoading(true);
      const response = await feedbackAPI.getAll();
      setFeedbacks(response.data);
    } catch (error) {
      console.error('Error loading feedbacks:', error);
      setError('Failed to load feedbacks. Using demo data.');
      // Fallback to demo data
      setFeedbacks([
        {
          _id: '1',
          student: 'Emma Thompson',
          avatar: 'https://images.unsplash.com/photo-1544725176-7c40e5a71c5e?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&q=80',
          course: 'Mathematics 101',
          rating: 5,
          text: 'Professor Johnson explains complex concepts in a way that\'s easy to understand.',
          date: '2023-10-15'
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewFeedback({
      ...newFeedback,
      [name]: value
    });
  };

  const handleRatingClick = (rating) => {
    setNewFeedback({
      ...newFeedback,
      rating: rating
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setError('');
      const response = await feedbackAPI.create(newFeedback);

      setFeedbacks([response.data, ...feedbacks]);

      setNewFeedback({
        student: '',
        course: '',
        rating: 0,
        text: '',
        teacherId: '654d4b5e8a1b2c3d4e5f6a7b'
      });
      setShowForm(false);

    } catch (error) {
      console.error('Error creating feedback:', error);
      setError('Failed to save feedback. Please try again.');
    }
  };

  const handleCancel = () => {
    setNewFeedback({
      student: '',
      course: '',
      rating: 0,
      text: '',
      teacherId: '654d4b5e8a1b2c3d4e5f6a7b'
    });
    setShowForm(false);
    setError('');
  };

  const renderStars = (rating) => {
    return [...Array(5)].map((_, i) => (
      <span key={i}>{i < rating ? 'â˜…' : 'â˜†'}</span>
    ));
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  if (!isAdmin) {
    return (
      <FeedbackContainer>
        <Container>
          <AccessDenied>
            <div style={{ fontSize: '48px', marginBottom: '20px' }}>ðŸ”’</div>
            <h3>Access Restricted</h3>
            <p>Only administrator users can access feedback management.</p>
            <p>Please contact your system administrator for access.</p>
          </AccessDenied>
        </Container>
      </FeedbackContainer>
    );
  }

  if (loading) {
    return (
      <FeedbackContainer>
        <Container>
          <SectionTitle>Student Feedback</SectionTitle>
          <div style={{ textAlign: 'center', padding: '40px' }}>
            <p>Loading feedbacks...</p>
          </div>
        </Container>
      </FeedbackContainer>
    );
  }

  return (
    <FeedbackContainer>
      <Container>
        <SectionTitle>Student Feedback</SectionTitle>

        {error && (
          <div style={{
            background: '#fee',
            color: '#c53030',
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            border: '1px solid #feb2b2'
          }}>
            {error}
          </div>
        )}

        <AddFeedbackButton onClick={() => setShowForm(!showForm)}>
          {showForm ? 'Cancel' : '+ Add New Feedback'}
        </AddFeedbackButton>

        {showForm && (
          <FeedbackForm>
            <FormTitle>Add New Feedback</FormTitle>
            <form onSubmit={handleSubmit}>
              {/* Form content remains the same */}
            </form>
          </FeedbackForm>
        )}

        <FeedbackGrid>
          {feedbacks.map(item => (
            <FeedbackCard key={item._id || item.id}>
              <StudentInfo>
                <StudentAvatar src={item.avatar} alt={item.student} />
                <div>
                  <StudentName>{item.student}</StudentName>
                  <CourseName>{item.course}</CourseName>
                </div>
              </StudentInfo>

              <Rating>
                {renderStars(item.rating)}
              </Rating>

              <FeedbackText>"{item.text}"</FeedbackText>

              <DateText>{formatDate(item.date)}</DateText>
            </FeedbackCard>
          ))}
        </FeedbackGrid>

        {feedbacks.length === 0 && (
          <div style={{ textAlign: 'center', padding: '40px', color: '#666' }}>
            <p>No feedback available yet.</p>
            <p>Click "Add New Feedback" to create the first one!</p>
          </div>
        )}
      </Container>
    </FeedbackContainer>
  );
};

export default Feedback;
