import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Container, Grid } from '../styles/globalStyles';
import ProfileUpload from '../components/ProfileUpload';
import { teachersAPI } from '../services/api';

const TeachersContainer = styled.div`
  padding: 20px;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: 700;
`;

const TeacherCard = styled.div`
  background: ${props => props.theme.cardBg};
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 20px ${props => props.theme.shadow};
  border: 1px solid ${props => props.theme.border};
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  animation: fadeIn 0.6s ease forwards;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px ${props => props.theme.shadow};
  }
`;

const TeacherAvatar = styled.img`
  width: 80px;
  height: 80px;
  border-radius: 50%;
  object-fit: cover;
  margin-bottom: 15px;
  border: 3px solid ${props => props.theme.primary};
`;

const TeacherName = styled.h3`
  color: ${props => props.theme.text};
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 5px;
`;

const TeacherSubject = styled.p`
  color: ${props => props.theme.text};
  opacity: 0.7;
  margin-bottom: 10px;
`;

const TeacherStats = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 15px;
`;

const Stat = styled.div`
  text-align: center;
`;

const StatValue = styled.div`
  font-size: 18px;
  font-weight: 700;
  color: ${props => props.theme.primary};
`;

const StatLabel = styled.div`
  font-size: 12px;
  color: ${props => props.theme.text};
  opacity: 0.7;
`;

const AddTeacherButton = styled.button`
  background: linear-gradient(45deg, ${props => props.theme.primary}, ${props => props.theme.accent});
  color: white;
  border: none;
  border-radius: 8px;
  padding: 12px 20px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 20px;
  transition: all 0.3s ease;

  &:hover {
    opacity: 0.9;
    transform: translateY(-2px);
    box-shadow: 0 8px 20px ${props => props.theme.primary}40;
  }
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 40px;
  color: ${props => props.theme.text};
  opacity: 0.7;
`;

const ErrorMessage = styled.div`
  background: #fee;
  color: #c53030;
  padding: 12px;
  border-radius: 8px;
  border: 1px solid #feb2b2;
  margin-bottom: 20px;
  text-align: center;
`;

const Teachers = ({ user }) => {
  const [teachers, setTeachers] = useState([]);
  const [showUpload, setShowUpload] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Load teachers from backend
  useEffect(() => {
    loadTeachers();
  }, []);

  const loadTeachers = async () => {
    try {
      setLoading(true);
      const response = await teachersAPI.getAll();
      setTeachers(response.data);
    } catch (error) {
      console.error('Error loading teachers:', error);
      setError('Failed to load teachers. Using demo data.');
      // Fallback to demo data
      setTeachers([
        {
          _id: '1',
          name: 'Sarah Johnson',
          subject: 'Mathematics',
          avatar: 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-4.0.3&auto=format&fit=crop&w=200&q=80',
          performance: 92,
          students: 28,
          years: 5
        },
        {
          _id: '2',
          name: 'Michael Chen',
          subject: 'Science',
          avatar: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&auto=format&fit=crop&w=200&q=80',
          performance: 88,
          students: 32,
          years: 3
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleTeacherAdded = (newTeacher) => {
    // Add the new teacher to the list
    setTeachers([newTeacher, ...teachers]);
    setShowUpload(false);
  };

  if (loading) {
    return (
      <TeachersContainer>
        <Container>
          <SectionTitle>Teacher Management</SectionTitle>
          <LoadingMessage>Loading teachers...</LoadingMessage>
        </Container>
      </TeachersContainer>
    );
  }

  return (
    <TeachersContainer>
      <Container>
        <SectionTitle>Teacher Management</SectionTitle>

        {error && (
          <ErrorMessage>{error}</ErrorMessage>
        )}

        {user?.role === 'admin' && (
          <AddTeacherButton onClick={() => setShowUpload(true)}>
            + Add New Teacher
          </AddTeacherButton>
        )}

        {showUpload && (
          <ProfileUpload 
            onClose={() => setShowUpload(false)} 
            onTeacherAdded={handleTeacherAdded}
          />
        )}

        <Grid>
          {teachers.map(teacher => (
            <TeacherCard key={teacher._id || teacher.id}>
              <div style={{ textAlign: 'center' }}>
                <TeacherAvatar src={teacher.avatar} alt={teacher.name} />
                <TeacherName>{teacher.name}</TeacherName>
                <TeacherSubject>{teacher.subject}</TeacherSubject>
              </div>

              <TeacherStats>
                <Stat>
                  <StatValue>{teacher.performance || 'N/A'}%</StatValue>
                  <StatLabel>Performance</StatLabel>
                </Stat>
                <Stat>
                  <StatValue>{teacher.students || 'N/A'}</StatValue>
                  <StatLabel>Students</StatLabel>
                </Stat>
                <Stat>
                  <StatValue>{teacher.years || 'N/A'}</StatValue>
                  <StatLabel>Years</StatLabel>
                </Stat>
              </TeacherStats>
            </TeacherCard>
          ))}
        </Grid>

        {teachers.length === 0 && (
          <div style={{ textAlign: 'center', padding: '40px', color: '#666' }}>
            <p>No teachers found.</p>
            {user?.role === 'admin' && (
              <p>Click "Add New Teacher" to add the first teacher!</p>
            )}
          </div>
        )}
      </Container>
    </TeachersContainer>
  );
};

export default Teachers;
