const Profile = ({ user: currentUser, onUpdateUser }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState(null); // separate form data
  const [loading, setLoading] = useState(true);
  const [showSuccess, setShowSuccess] = useState(false);

  const [activities] = useState([
    { id: 1, type: 'login', text: 'Logged into the system', time: '2 hours ago', icon: 'üîê', color: '#10b981' },
    { id: 2, type: 'profile_update', text: 'Updated profile information', time: 'Yesterday', icon: 'üìù', color: '#3b82f6' },
    { id: 3, type: 'feedback', text: 'Submitted student feedback', time: '2 days ago', icon: 'üí¨', color: '#8b5cf6' },
    { id: 4, type: 'view', text: 'Viewed performance analytics', time: '3 days ago', icon: 'üìä', color: '#f59e0b' }
  ]);

  // Load user data from localStorage on mount
  useEffect(() => {
    try {
      const storedUser = localStorage.getItem('adminUser');
      let parsedUser;
      if (storedUser) {
        parsedUser = JSON.parse(storedUser);
      } else if (currentUser) {
        parsedUser = currentUser;
        localStorage.setItem('adminUser', JSON.stringify(currentUser));
      } else {
        parsedUser = {
          id: '1',
          name: 'Admin User',
          email: 'admin@edutrack.com',
          role: 'admin',
          avatar:
            'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-4.0.3&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80',
          phone: '',
          department: 'Administration',
          joinDate: new Date().toISOString().split('T')[0]
        };
        localStorage.setItem('adminUser', JSON.stringify(parsedUser));
      }

      setUser(parsedUser);
      if (onUpdateUser) onUpdateUser(parsedUser);
    } catch (err) {
      console.error('Error loading user data:', err);
    } finally {
      setLoading(false);
    }
  }, [currentUser, onUpdateUser]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSave = () => {
    try {
      setUser(formData);
      localStorage.setItem('adminUser', JSON.stringify(formData));
      if (onUpdateUser) onUpdateUser(formData);

      setIsEditing(false);
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 3000);
    } catch (err) {
      console.error('Error saving user data:', err);
      alert('Error saving profile. Please try again.');
    }
  };

  const handleCancel = () => {
    setFormData(user);
    setIsEditing(false);
  };

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const updatedUser = { ...user, avatar: reader.result };
        setUser(updatedUser);
        setFormData(updatedUser);

        localStorage.setItem('adminUser', JSON.stringify(updatedUser));
        if (onUpdateUser) onUpdateUser(updatedUser);

        setShowSuccess(true);
        setTimeout(() => setShowSuccess(false), 3000);
      };
      reader.readAsDataURL(file);
    }
  };

  if (loading || !user) {
    return (
      <ProfileContainer>
        <Container>
          <LoadingMessage>Loading profile...</LoadingMessage>
        </Container>
      </ProfileContainer>
    );
  }

  return (
    <ProfileContainer>
      <Container>
        <SectionTitle>User Profile</SectionTitle>

        {showSuccess && (
          <SuccessMessage>
            ‚úÖ Profile updated successfully! Changes have been saved.
          </SuccessMessage>
        )}

        <ProfileGrid>
          {/* Left Column */}
          <div>
            <ProfileCard>
              <CardTitle>üë§ Profile Information</CardTitle>

              <AvatarContainer>
                <Avatar src={user.avatar} alt={user.name} />
                <AvatarUpload>
                  üì∑ Change Photo
                  <input type="file" accept="image/*" onChange={handleAvatarChange} />
                </AvatarUpload>
              </AvatarContainer>

              <UserInfo>
                <InfoItem>
                  <InfoLabel>Name:</InfoLabel>
                  <InfoValue>{user.name}</InfoValue>
                </InfoItem>
                <InfoItem>
                  <InfoLabel>Email:</InfoLabel>
                  <InfoValue>{user.email}</InfoValue>
                </InfoItem>
                <InfoItem>
                  <InfoLabel>Role:</InfoLabel>
                  <InfoValue>
                    {user.role || 'admin'}
                    <RoleBadge role={user.role || 'admin'}>
                      {(user.role || 'admin').toUpperCase()}
                    </RoleBadge>
                  </InfoValue>
                </InfoItem>
                <InfoItem>
                  <InfoLabel>Member since:</InfoLabel>
                  <InfoValue>{user.joinDate || 'N/A'}</InfoValue>
                </InfoItem>
                {user.phone && (
                  <InfoItem>
                    <InfoLabel>Phone:</InfoLabel>
                    <InfoValue>{user.phone}</InfoValue>
                  </InfoItem>
                )}
                {user.department && (
                  <InfoItem>
                    <InfoLabel>Department:</InfoLabel>
                    <InfoValue>{user.department}</InfoValue>
                  </InfoItem>
                )}
              </UserInfo>
            </ProfileCard>
          </div>

          {/* Right Column */}
          <div>
            <ProfileCard>
              <CardTitle>‚öôÔ∏è Edit Profile</CardTitle>

              <FormGroup>
                <FormLabel>Full Name</FormLabel>
                <FormInput
                  type="text"
                  name="name"
                  value={isEditing ? formData?.name || '' : user.name}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                />
              </FormGroup>

              <FormGroup>
                <FormLabel>Email Address</FormLabel>
                <FormInput
                  type="email"
                  name="email"
                  value={isEditing ? formData?.email || '' : user.email}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                />
              </FormGroup>

              <FormGroup>
                <FormLabel>Phone Number</FormLabel>
                <FormInput
                  type="tel"
                  name="phone"
                  value={isEditing ? formData?.phone || '' : user.phone}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  placeholder="Enter phone number"
                />
              </FormGroup>

              <FormGroup>
                <FormLabel>Department</FormLabel>
                <FormInput
                  type="text"
                  name="department"
                  value={isEditing ? formData?.department || '' : user.department}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  placeholder="Enter department"
                />
              </FormGroup>

              {user.role === 'admin' && (
                <FormGroup>
                  <FormLabel>User Role</FormLabel>
                  <FormSelect
                    name="role"
                    value={isEditing ? formData?.role || 'admin' : user.role}
                    onChange={handleInputChange}
                    disabled={!isEditing}
                  >
                    <option value="admin">Administrator</option>
                    <option value="teacher">Teacher</option>
                    <option value="user">User</option>
                  </FormSelect>
                </FormGroup>
              )}

              <FormActions>
                {!isEditing ? (
                  <SaveButton onClick={() => { setFormData(user); setIsEditing(true); }}>
                    ‚úèÔ∏è Edit Profile
                  </SaveButton>
                ) : (
                  <>
                    <SaveButton onClick={handleSave}>üíæ Save Changes</SaveButton>
                    <CancelButton onClick={handleCancel}>‚ùå Cancel</CancelButton>
                  </>
                )}
              </FormActions>
            </ProfileCard>

            <ProfileCard style={{ marginTop: '30px' }}>
              <CardTitle>üìã Recent Activities</CardTitle>
              <ActivityList>
                {activities.map((activity) => (
                  <ActivityItem key={activity.id} color={activity.color}>
                    <ActivityIcon color={activity.color}>{activity.icon}</ActivityIcon>
                    <ActivityContent>
                      <ActivityText>{activity.text}</ActivityText>
                      <ActivityTime>{activity.time}</ActivityTime>
                    </ActivityContent>
                  </ActivityItem>
                ))}
              </ActivityList>
            </ProfileCard>
          </div>
        </ProfileGrid>
      </Container>
    </ProfileContainer>
  );
};
