import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Container, Grid } from '../styles/globalStyles';

const PerformanceContainer = styled.div`
  padding: 20px;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: 700;
`;

const PerformanceCard = styled.div`
  background: ${props => props.theme.cardBg};
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 20px ${props => props.theme.shadow};
  border: 1px solid ${props => props.theme.border};
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  animation: fadeIn 0.6s ease forwards;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px ${props => props.theme.shadow};
  }
`;

const TeacherName = styled.h3`
  color: ${props => props.theme.text};
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 10px;
`;

const PerformanceDetails = styled.p`
  color: ${props => props.theme.text};
  opacity: 0.8;
  margin-bottom: 8px;
  line-height: 1.5;
`;

const Rating = styled.div`
  display: inline-block;
  background: ${props => {
    if (props.rating >= 4) return '#10b981';
    if (props.rating >= 3) return '#f59e0b';
    return '#ef4444';
  }};
  color: white;
  padding: 4px 12px;
  border-radius: 20px;
  font-size: 14px;
  font-weight: 600;
`;

const AccessDeniedMessage = styled.div`
  text-align: center;
  padding: 40px;
  background-color: #fee2e2;
  color: #b91c1c;
  border-radius: 8px;
  margin: 20px 0;
  border: 1px solid #fecaca;
`;

const AddButton = styled.button`
  background: ${props => props.theme.primary};
  color: white;
  border: none;
  border-radius: 8px;
  padding: 12px 24px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 20px;
  transition: all 0.3s ease;

  &:hover {
    opacity: 0.9;
    transform: translateY(-2px);
  }

  ${props => props.disabled && `
    background: #ccc;
    cursor: not-allowed;

    &:hover {
      opacity: 1;
      transform: none;
    }
  `}
`;

// Helper function to get current user
const getCurrentUser = () => {
  try {
    const user = localStorage.getItem('currentUser');
    return user ? JSON.parse(user) : null;
  } catch (error) {
    console.error('Error loading current user from localStorage:', error);
    return null;
  }
};

const Performance = () => {
  const [performanceData] = useState([
    {
      id: 1,
      teacherName: 'John Smith',
      subject: 'Mathematics',
      rating: 4.5,
      date: '2023-11-15',
      comments: 'Excellent classroom management and student engagement'
    },
    {
      id: 2,
      teacherName: 'Sarah Johnson',
      subject: 'Science',
      rating: 4.2,
      date: '2023-11-10',
      comments: 'Great use of practical experiments in lessons'
    },
    {
      id: 3,
      teacherName: 'Mike Wilson',
      subject: 'English',
      rating: 3.8,
      date: '2023-11-08',
      comments: 'Good communication skills, could improve assessment methods'
    },
    {
      id: 4,
      teacherName: 'Emily Davis',
      subject: 'History',
      rating: 4.7,
      date: '2023-11-12',
      comments: 'Outstanding lesson planning and student feedback'
    },
    {
      id: 5,
      teacherName: 'David Brown',
      subject: 'Physics',
      rating: 3.5,
      date: '2023-11-05',
      comments: 'Needs to improve student participation in class'
    },
    {
      id: 6,
      teacherName: 'Lisa Anderson',
      subject: 'Art',
      rating: 4.8,
      date: '2023-11-18',
      comments: 'Creative approach to teaching, students are highly engaged'
    }
  ]);

  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const user = getCurrentUser();
    setCurrentUser(user);
  }, []);

  // Show access denied for non-admin users
  if (currentUser && currentUser.role !== 'admin') {
    return (
      <PerformanceContainer>
        <Container>
          <SectionTitle>Performance Tracking</SectionTitle>
          <AccessDeniedMessage>
            <h3>Access Denied</h3>
            <p>You don't have permission to view performance data. Please contact an administrator.</p>
          </AccessDeniedMessage>
        </Container>
      </PerformanceContainer>
    );
  }

  return (
    <PerformanceContainer>
      <Container>
        <SectionTitle>Performance Tracking</SectionTitle>

        {currentUser && currentUser.role === 'admin' && (
          <AddButton onClick={() => alert('Add performance record functionality would go here')}>
            + Add Performance Record
          </AddButton>
        )}

        <Grid>
          {performanceData.map(record => (
            <PerformanceCard key={record.id}>
              <TeacherName>{record.teacherName}</TeacherName>
              <PerformanceDetails><strong>Subject:</strong> {record.subject}</PerformanceDetails>
              <PerformanceDetails><strong>Date:</strong> {record.date}</PerformanceDetails>
              <PerformanceDetails><strong>Rating:</strong> <Rating rating={record.rating}>{record.rating}/5</Rating></PerformanceDetails>
              <PerformanceDetails><strong>Comments:</strong> {record.comments}</PerformanceDetails>
            </PerformanceCard>
          ))}
        </Grid>
      </Container>
    </PerformanceContainer>
  );
};

export default Performance;
