import React, { useState } from 'react';
import styled from 'styled-components';
import { Container } from '../styles/globalStyles';
import PerformanceChart from '../components/PerformanceChart';

const PerformanceContainer = styled.div`
  padding: 20px;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: 700;
`;

const FilterSection = styled.div`
  display: flex;
  gap: 15px;
  margin-bottom: 20px;
  flex-wrap: wrap;
`;

const FilterButton = styled.button`
  background: ${props => props.active ? props.theme.primary : props.theme.cardBg};
  color: ${props => props.active ? 'white' : props.theme.text};
  border: 1px solid ${props => props.theme.border};
  border-radius: 20px;
  padding: 8px 16px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.theme.primary};
    color: white;
  }
`;

const PerformanceMetrics = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-bottom: 30px;
`;

const MetricCard = styled.div`
  background: ${props => props.theme.cardBg};
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 20px ${props => props.theme.shadow};
  border: 1px solid ${props => props.theme.border};
  text-align: center;
`;

const MetricTitle = styled.h3`
  color: ${props => props.theme.text};
  font-size: 16px;
  margin-bottom: 10px;
  opacity: 0.8;
`;

const MetricValue = styled.div`
  font-size: 32px;
  font-weight: 700;
  color: ${props => props.theme.primary};
`;

const Performance = () => {
  const [activeFilter, setActiveFilter] = useState('all');

  const filters = [
    { id: 'all', label: 'All Teachers' },
    { id: 'math', label: 'Mathematics' },
    { id: 'science', label: 'Science' },
    { id: 'english', label: 'English' },
    { id: 'history', label: 'History' },
    { id: 'arts', label: 'Arts' }
  ];

  const metrics = [
    { title: 'Average Performance Score', value: '87%' },
    { title: 'Top Performing Subject', value: 'Mathematics' },
    { title: 'Improvement Rate', value: '+5.2%' },
    { title: 'Feedback Response Rate', value: '92%' }
  ];

  return (
    <PerformanceContainer>
      <Container>
        <SectionTitle>Performance Analytics</SectionTitle>

        <FilterSection>
          {filters.map(filter => (
            <FilterButton
              key={filter.id}
              active={activeFilter === filter.id}
              onClick={() => setActiveFilter(filter.id)}
            >
              {filter.label}
            </FilterButton>
          ))}
        </FilterSection>

        <PerformanceMetrics>
          {metrics.map((metric, index) => (
            <MetricCard key={index}>
              <MetricTitle>{metric.title}</MetricTitle>
              <MetricValue>{metric.value}</MetricValue>
            </MetricCard>
          ))}
        </PerformanceMetrics>

        <PerformanceChart />
      </Container>
    </PerformanceContainer>
  );
};

export default Performance;
