import React from 'react';
import styled from 'styled-components';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const ChartContainer = styled.div`
  background: ${props => props.theme.cardBg};
  border-radius: 12px;
  padding: 25px;
  box-shadow: 0 4px 20px ${props => props.theme.shadow};
  border: 1px solid ${props => props.theme.border};
  margin-bottom: 30px;
`;

const ChartTitle = styled.h3`
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  font-size: 18px;
  font-weight: 600;
  text-align: center;
`;

const PerformanceChart = ({ subject, performanceData, isDashboard = false }) => {
  // For dashboard view - show sample data
  if (isDashboard) {
    const data = {
      labels: ['Mathematics', 'Science', 'English', 'History', 'Arts'],
      datasets: [
        {
          label: 'Average Performance (%)',
          data: [92, 85, 82, 78, 91],
          backgroundColor: 'rgba(59, 130, 246, 0.8)',
          borderColor: 'rgba(59, 130, 246, 1)',
          borderWidth: 1,
        },
      ],
    };

    const options = {
      responsive: true,
      plugins: {
        legend: {
          position: 'top',
        },
        title: {
          display: true,
          text: 'Subject Performance Overview',
        },
      },
      scales: {
        y: {
          beginAtZero: true,
          max: 100,
          ticks: {
            callback: function(value) {
              return value + '%';
            }
          }
        },
      },
    };

    return (
      <ChartContainer>
        <ChartTitle>Performance Overview</ChartTitle>
        <Bar data={data} options={options} />
      </ChartContainer>
    );
  }

  // For performance page view - use the provided data
  // Generate chart data based on performance metrics
  const getChartData = () => {
    if (!performanceData || !performanceData.metrics) {
      return {
        labels: ['No Data'],
        datasets: [
          {
            label: 'Performance',
            data: [0],
            backgroundColor: 'rgba(100, 100, 100, 0.5)',
          },
        ],
      };
    }

    const labels = performanceData.metrics.map(metric => metric.title);
    const data = performanceData.metrics.map(metric => {
      // Extract numeric value from string (e.g., "87%" -> 87, "+5.2%" -> 5.2)
      const numericValue = parseFloat(metric.value.replace(/[+%]/g, ''));
      return isNaN(numericValue) ? 0 : numericValue;
    });

    return {
      labels,
      datasets: [
        {
          label: 'Performance Score',
          data,
          backgroundColor: 'rgba(59, 130, 246, 0.8)',
          borderColor: 'rgba(59, 130, 246, 1)',
          borderWidth: 1,
        },
      ],
    };
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: `Performance Metrics - ${subject ? subject.charAt(0).toUpperCase() + subject.slice(1) : 'All Subjects'}`,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        max: 100,
        ticks: {
          callback: function(value) {
            return value + '%';
          }
        }
      },
    },
  };

  return (
    <ChartContainer>
      <ChartTitle>Performance Overview</ChartTitle>
      <Bar data={getChartData()} options={options} />
    </ChartContainer>
  );
};

export default PerformanceChart;
