import React, { useState } from 'react';
import styled from 'styled-components';
import { Container, Grid } from '../styles/globalStyles';

const TeachersContainer = styled.div`
  padding: 20px;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: 700;
`;

const TeacherCard = styled.div`
  background: ${props => props.theme.cardBg};
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 20px ${props => props.theme.shadow};
  border: 1px solid ${props => props.theme.border};
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  animation: fadeIn 0.6s ease forwards;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px ${props => props.theme.shadow};
  }
`;

const TeacherName = styled.h3`
  color: ${props => props.theme.text};
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 10px;
`;

const TeacherDetails = styled.p`
  color: ${props => props.theme.text};
  opacity: 0.8;
  margin-bottom: 8px;
  line-height: 1.5;
`;

const SubjectBadge = styled.span`
  display: inline-block;
  background: ${props => props.theme.primary};
  color: white;
  padding: 4px 12px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 600;
  margin-top: 10px;
`;

const Teachers = () => {
  const [teachers] = useState([
    {
      id: 1,
      name: 'John Smith',
      subject: 'Mathematics',
      email: 'john.smith@school.edu',
      phone: '+1 (555) 123-4567',
      experience: '8 years'
    },
    {
      id: 2,
      name: 'Sarah Johnson',
      subject: 'Science',
      email: 'sarah.johnson@school.edu',
      phone: '+1 (555) 123-4568',
      experience: '12 years'
    },
    {
      id: 3,
      name: 'Mike Wilson',
      subject: 'English',
      email: 'mike.wilson@school.edu',
      phone: '+1 (555) 123-4569',
      experience: '6 years'
    },
    {
      id: 4,
      name: 'Emily Davis',
      subject: 'History',
      email: 'emily.davis@school.edu',
      phone: '+1 (555) 123-4570',
      experience: '10 years'
    },
    {
      id: 5,
      name: 'David Brown',
      subject: 'Physics',
      email: 'david.brown@school.edu',
      phone: '+1 (555) 123-4571',
      experience: '15 years'
    },
    {
      id: 6,
      name: 'Lisa Anderson',
      subject: 'Art',
      email: 'lisa.anderson@school.edu',
      phone: '+1 (555) 123-4572',
      experience: '7 years'
    }
  ]);

  return (
    <TeachersContainer>
      <Container>
        <SectionTitle>Teaching Staff</SectionTitle>

        <Grid>
          {teachers.map(teacher => (
            <TeacherCard key={teacher.id}>
              <TeacherName>{teacher.name}</TeacherName>
              <TeacherDetails><strong>Email:</strong> {teacher.email}</TeacherDetails>
              <TeacherDetails><strong>Phone:</strong> {teacher.phone}</TeacherDetails>
              <TeacherDetails><strong>Experience:</strong> {teacher.experience}</TeacherDetails>
              <SubjectBadge>{teacher.subject}</SubjectBadge>
            </TeacherCard>
          ))}
        </Grid>
      </Container>
    </TeachersContainer>
  );
};

export default Teachers;
