import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const SidebarContainer = styled.div`
  width: 250px;
  background: ${props => props.theme.cardBg};
  padding: 20px;
  display: flex;
  flex-direction: column;
  height: 100vh;
`;

const NavItem = styled(NavLink)`
  padding: 12px 16px;
  border-radius: 8px;
  margin-bottom: 10px;
  text-decoration: none;
  color: ${props => props.theme.text};
  font-weight: 500;
  transition: all 0.3s ease;

  &.active {
    background: linear-gradient(45deg, ${props => props.theme.primary}, ${props => props.theme.accent});
    color: white;
  }

  &:hover {
    background: ${props => props.theme.hoverBg};
    transform: translateX(5px);
  }
`;

const Sidebar = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login'); // redirect
  };

  return (
    <SidebarContainer>
      <NavItem to="/dashboard">Dashboard</NavItem>
      <NavItem to="/performance">Performance</NavItem>
      <NavItem to="/training">Training</NavItem>
      <NavItem to="/milestones">Milestones</NavItem>
      <NavItem to="/feedback">Feedback</NavItem>
      <NavItem to="/teacher">Teacher</NavItem>

      {/* âœ… Logout button styled same as others */}
      <NavItem as="button" onClick={handleLogout} style={{ textAlign: 'left', border: 'none', background: 'transparent', cursor: 'pointer' }}>
        Logout
      </NavItem>
    </SidebarContainer>
  );
};

export default Sidebar;
