import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Container, Grid } from '../styles/globalStyles';

const TrainingContainer = styled.div`
  padding: 20px;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: 700;
`;

const TrainingGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 30px;

  @media (max-width: 968px) {
    grid-template-columns: 1fr;
  }
`;

const TrainingCard = styled.div`
  background: ${props => props.theme.cardBg};
  border-radius: 12px;
  padding: 25px;
  box-shadow: 0 4px 20px ${props => props.theme.shadow};
  border: 1px solid ${props => props.theme.border};
  margin-bottom: 30px;
`;

const CardTitle = styled.h3`
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  font-size: 18px;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 10px;
`;

const ProgramList = styled.div`
  margin-top: 20px;
`;

const ProgramItem = styled.div`
  padding: 15px;
  background: ${props => props.theme.background};
  border-radius: 8px;
  margin-bottom: 15px;
  border-left: 4px solid ${props => {
    switch(props.status) {
      case 'completed': return '#10b981';
      case 'ongoing': return '#f59e0b';
      case 'upcoming': return '#3b82f6';
      default: return '#6b7280';
    }
  }};
`;

const ProgramTitle = styled.h4`
  color: ${props => props.theme.text};
  margin-bottom: 8px;
  font-size: 16px;
`;

const ProgramDetails = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
`;

const ProgramDate = styled.span`
  color: ${props => props.theme.text};
  opacity: 0.7;
  font-size: 14px;
`;

const ProgramStatus = styled.span`
  padding: 4px 12px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 600;
  background: ${props => {
    switch(props.status) {
      case 'completed': return '#d1fae5';
      case 'ongoing': return '#fef3c7';
      case 'upcoming': return '#dbeafe';
      default: return '#f3f4f6';
    }
  }};
  color: ${props => {
    switch(props.status) {
      case 'completed': return '#065f46';
      case 'ongoing': return '#92400e';
      case 'upcoming': return '#1e40af';
      default: return '#374151';
    }
  }};
`;

const ProgramParticipants = styled.div`
  margin-top: 10px;
`;

const ParticipantList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-top: 8px;
`;

const Participant = styled.span`
  background: ${props => props.theme.primary}20;
  color: ${props => props.theme.primary};
  padding: 4px 10px;
  border-radius: 15px;
  font-size: 12px;
  font-weight: 500;
`;

const AddButton = styled.button`
  background: ${props => props.theme.primary};
  color: white;
  border: none;
  border-radius: 8px;
  padding: 10px 20px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  margin-top: 15px;
  transition: all 0.3s ease;

  &:hover {
    opacity: 0.9;
    transform: translateY(-2px);
  }
`;

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: ${props => props.theme.cardBg};
  border-radius: 12px;
  padding: 30px;
  width: 90%;
  max-width: 500px;
  max-height: 80vh;
  overflow-y: auto;
  box-shadow: 0 20px 40px ${props => props.theme.shadow};
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
`;

const FormLabel = styled.label`
  display: block;
  margin-bottom: 8px;
  font-weight: 600;
  color: ${props => props.theme.text};
`;

const FormInput = styled.input`
  width: 100%;
  padding: 12px;
  border: 1px solid ${props => props.theme.border};
  border-radius: 8px;
  background: ${props => props.theme.background};
  color: ${props => props.theme.text};
  font-size: 16px;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.primary};
    box-shadow: 0 0 0 2px ${props => props.theme.primary}20;
  }
`;

const FormSelect = styled.select`
  width: 100%;
  padding: 12px;
  border: 1px solid ${props => props.theme.border};
  border-radius: 8px;
  background: ${props => props.theme.background};
  color: ${props => props.theme.text};
  font-size: 16px;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.primary};
    box-shadow: 0 0 0 2px ${props => props.theme.primary}20;
  }
`;

const FormActions = styled.div`
  display: flex;
  gap: 15px;
  margin-top: 25px;
  justify-content: flex-end;
`;

const SaveButton = styled.button`
  background: ${props => props.theme.primary};
  color: white;
  border: none;
  border-radius: 8px;
  padding: 12px 24px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    opacity: 0.9;
    transform: translateY(-2px);
  }
`;

const CancelButton = styled.button`
  background: ${props => props.theme.background};
  color: ${props => props.theme.text};
  border: 1px solid ${props => props.theme.border};
  border-radius: 8px;
  padding: 12px 24px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.theme.border};
  }
`;

const TeacherCheckbox = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 10px;

  input {
    margin-right: 10px;
  }

  label {
    color: ${props => props.theme.text};
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 40px;
  color: ${props => props.theme.text};
  opacity: 0.7;

  p {
    margin-bottom: 20px;
  }
`;

// Local storage functions
const getStoredTrainingData = () => {
  try {
    const storedData = localStorage.getItem('trainingData');
    return storedData ? JSON.parse(storedData) : null;
  } catch (error) {
    console.error('Error loading training data from localStorage:', error);
    return null;
  }
};

const setStoredTrainingData = (data) => {
  try {
    localStorage.setItem('trainingData', JSON.stringify(data));
  } catch (error) {
    console.error('Error saving training data to localStorage:', error);
  }
};

const getStoredTeachers = () => {
  try {
    const storedTeachers = localStorage.getItem('teachers');
    return storedTeachers ? JSON.parse(storedTeachers) : [];
  } catch (error) {
    console.error('Error loading teachers from localStorage:', error);
    return [];
  }
};

// Default training data
const getDefaultTrainingData = () => ({
  programs: [
    {
      id: 1,
      title: 'Classroom Management Techniques',
      date: '2023-06-15',
      duration: '2 days',
      status: 'completed',
      participants: [1, 2, 3]
    },
    {
      id: 2,
      title: 'Digital Teaching Tools Workshop',
      date: '2023-07-20',
      duration: '1 day',
      status: 'ongoing',
      participants: [2, 4]
    },
    {
      id: 3,
      title: 'Advanced Assessment Strategies',
      date: '2023-08-10',
      duration: '3 days',
      status: 'upcoming',
      participants: []
    },
    {
      id: 4,
      title: 'Special Education Training',
      date: '2023-09-05',
      duration: '5 days',
      status: 'upcoming',
      participants: [1, 3, 5]
    }
  ]
});

// Initialize training data
const initializeTrainingData = () => {
  const storedData = getStoredTrainingData();
  if (!storedData) {
    const defaultData = getDefaultTrainingData();
    setStoredTrainingData(defaultData);
    return defaultData;
  }
  return storedData;
};

const Training = () => {
  const [trainingData, setTrainingData] = useState(initializeTrainingData());
  const [teachers, setTeachers] = useState(getStoredTeachers());
  const [showModal, setShowModal] = useState(false);
  const [selectedProgram, setSelectedProgram] = useState(null);
  const [selectedTeachers, setSelectedTeachers] = useState([]);

  // Update localStorage whenever trainingData changes
  useEffect(() => {
    setStoredTrainingData(trainingData);
  }, [trainingData]);

  // Update teachers list when localStorage changes
  useEffect(() => {
    const storedTeachers = getStoredTeachers();
    setTeachers(storedTeachers);
  }, []);

  const getProgramsByStatus = (status) => {
    return trainingData.programs.filter(program => program.status === status);
  };

  const getTeacherName = (teacherId) => {
    const teacher = teachers.find(t => t.id === teacherId);
    return teacher ? teacher.name : 'Unknown Teacher';
  };

  const handleAddParticipants = (program) => {
    setSelectedProgram(program);
    setSelectedTeachers(program.participants || []);
    setShowModal(true);
  };

  const handleTeacherSelection = (teacherId) => {
    setSelectedTeachers(prev => {
      if (prev.includes(teacherId)) {
        return prev.filter(id => id !== teacherId);
      } else {
        return [...prev, teacherId];
      }
    });
  };

  const handleSaveParticipants = () => {
    if (!selectedProgram) return;

    const updatedPrograms = trainingData.programs.map(program => {
      if (program.id === selectedProgram.id) {
        return {
          ...program,
          participants: selectedTeachers
        };
      }
      return program;
    });

    setTrainingData({
      ...trainingData,
      programs: updatedPrograms
    });

    setShowModal(false);
    setSelectedProgram(null);
    setSelectedTeachers([]);
  };

  const handleCancel = () => {
    setShowModal(false);
    setSelectedProgram(null);
    setSelectedTeachers([]);
  };

  const renderPrograms = (programs, status) => {
    if (programs.length === 0) {
      return (
        <EmptyState>
          <p>No {status} training programs.</p>
          <AddButton onClick={() => alert('Add new program functionality would go here')}>
            + Add New Program
          </AddButton>
        </EmptyState>
      );
    }

    return programs.map(program => (
      <ProgramItem key={program.id} status={program.status}>
        <ProgramTitle>{program.title}</ProgramTitle>
        <ProgramDetails>
          <ProgramDate>{program.date} â€¢ {program.duration}</ProgramDate>
          <ProgramStatus status={program.status}>
            {program.status.charAt(0).toUpperCase() + program.status.slice(1)}
          </ProgramStatus>
        </ProgramDetails>
        <ProgramParticipants>
          <strong>Participants:</strong>
          <ParticipantList>
            {program.participants && program.participants.length > 0 ? (
              program.participants.map(teacherId => (
                <Participant key={teacherId}>
                  {getTeacherName(teacherId)}
                </Participant>
              ))
            ) : (
              <span style={{ opacity: 0.7 }}>No participants yet</span>
            )}
          </ParticipantList>
        </ProgramParticipants>
        <AddButton onClick={() => handleAddParticipants(program)}>
          {program.participants && program.participants.length > 0 ? 'Edit Participants' : 'Add Participants'}
        </AddButton>
      </ProgramItem>
    ));
  };

  const completedPrograms = getProgramsByStatus('completed');
  const ongoingPrograms = getProgramsByStatus('ongoing');
  const upcomingPrograms = getProgramsByStatus('upcoming');

  return (
    <TrainingContainer>
      <Container>
        <SectionTitle>Training Programs</SectionTitle>

        <TrainingGrid>
          {/* Completed Programs */}
          <TrainingCard>
            <CardTitle>âœ… Completed Programs</CardTitle>
            <ProgramList>
              {renderPrograms(completedPrograms, 'completed')}
            </ProgramList>
          </TrainingCard>

          {/* Ongoing Programs */}
          <TrainingCard>
            <CardTitle>ðŸ”„ Ongoing Programs</CardTitle>
            <ProgramList>
              {renderPrograms(ongoingPrograms, 'ongoing')}
            </ProgramList>
          </TrainingCard>

          {/* Upcoming Programs */}
          <TrainingCard>
            <CardTitle>ðŸ“… Upcoming Programs</CardTitle>
            <ProgramList>
              {renderPrograms(upcomingPrograms, 'upcoming')}
            </ProgramList>
          </TrainingCard>

          {/* Program Statistics */}
          <TrainingCard>
            <CardTitle>ðŸ“Š Training Statistics</CardTitle>
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <h3 style={{ color: '#3b82f6', fontSize: '32px', marginBottom: '10px' }}>
                {trainingData.programs.length}
              </h3>
              <p style={{ color: '#6b7280' }}>Total Programs</p>

              <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '30px' }}>
                <div>
                  <h4 style={{ color: '#10b981', fontSize: '24px' }}>{completedPrograms.length}</h4>
                  <p style={{ color: '#6b7280', fontSize: '14px' }}>Completed</p>
                </div>
                <div>
                  <h4 style={{ color: '#f59e0b', fontSize: '24px' }}>{ongoingPrograms.length}</h4>
                  <p style={{ color: '#6b7280', fontSize: '14px' }}>Ongoing</p>
                </div>
                <div>
                  <h4 style={{ color: '#3b82f6', fontSize: '24px' }}>{upcomingPrograms.length}</h4>
                  <p style={{ color: '#6b7280', fontSize: '14px' }}>Upcoming</p>
                </div>
              </div>
            </div>
          </TrainingCard>
        </TrainingGrid>

        {/* Add Participants Modal */}
        {showModal && selectedProgram && (
          <ModalOverlay onClick={handleCancel}>
            <ModalContent onClick={(e) => e.stopPropagation()}>
              <CardTitle>ðŸ‘¥ Add Participants to {selectedProgram.title}</CardTitle>

              <FormGroup>
                <FormLabel>Select Teachers:</FormLabel>
                <div style={{ maxHeight: '200px', overflowY: 'auto' }}>
                  {teachers.map(teacher => (
                    <TeacherCheckbox key={teacher.id}>
                      <input
                        type="checkbox"
                        id={`teacher-${teacher.id}`}
                        checked={selectedTeachers.includes(teacher.id)}
                        onChange={() => handleTeacherSelection(teacher.id)}
                      />
                      <label htmlFor={`teacher-${teacher.id}`}>
                        {teacher.name} ({teacher.subject})
                      </label>
                    </TeacherCheckbox>
                  ))}
                </div>
              </FormGroup>

              <FormActions>
                <CancelButton onClick={handleCancel}>Cancel</CancelButton>
                <SaveButton onClick={handleSaveParticipants}>Save Participants</SaveButton>
              </FormActions>
            </ModalContent>
          </ModalOverlay>
        )}
      </Container>
    </TrainingContainer>
  );
};

export default Training;
