import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const TeachersContainer = styled.div`
  padding: 20px;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: 700;
`;

const AddButton = styled.button`
  background: ${props => props.theme.primary};
  color: white;
  border: none;
  border-radius: 8px;
  padding: 12px 24px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 20px;
  transition: all 0.3s ease;

  &:hover {
    opacity: 0.9;
    transform: translateY(-2px);
  }

  ${props => props.disabled && `
    background: #ccc;
    cursor: not-allowed;

    &:hover {
      opacity: 1;
      transform: none;
    }
  `}
`;

const AccessDeniedMessage = styled.div`
  text-align: center;
  padding: 40px;
  background-color: #fee2e2;
  color: #b91c1c;
  border-radius: 8px;
  margin: 20px 0;
`;

const TeachersGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 20px;
`;

const TeacherCard = styled.div`
  background: ${props => props.theme.cardBg};
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 20px ${props => props.theme.shadow};
  border: 1px solid ${props => props.theme.border};
`;

const TeacherName = styled.h3`
  color: ${props => props.theme.text};
  margin-bottom: 10px;
`;

const TeacherDetails = styled.p`
  color: ${props => props.theme.text};
  opacity: 0.7;
  margin-bottom: 5px;
`;

const getStoredTeachers = () => {
  try {
    const storedTeachers = localStorage.getItem('teachers');
    return storedTeachers ? JSON.parse(storedTeachers) : [];
  } catch (error) {
    console.error('Error loading teachers from localStorage:', error);
    return [];
  }
};

const getCurrentUser = () => {
  try {
    const user = localStorage.getItem('currentUser');
    return user ? JSON.parse(user) : null;
  } catch (error) {
    console.error('Error loading current user from localStorage:', error);
    return null;
  }
};

const Teachers = () => {
  const [teachers, setTeachers] = useState([]);
  const [currentUser, setCurrentUser] = useState(getCurrentUser());

  useEffect(() => {
    const storedTeachers = getStoredTeachers();
    setTeachers(storedTeachers);
  }, []);

  useEffect(() => {
    const user = getCurrentUser();
    setCurrentUser(user);
  }, []);

  return (
    <TeachersContainer>
      <Container>
        <SectionTitle>Teachers</SectionTitle>

        {currentUser && currentUser.role === 'admin' ? (
          <>
            <AddButton onClick={() => alert('Add teacher functionality would go here')}>
              + Add New Teacher
            </AddButton>

            <TeachersGrid>
              {teachers.map(teacher => (
                <TeacherCard key={teacher.id}>
                  <TeacherName>{teacher.name}</TeacherName>
                  <TeacherDetails>Subject: {teacher.subject}</TeacherDetails>
                  <TeacherDetails>Email: {teacher.email}</TeacherDetails>
                  <TeacherDetails>Phone: {teacher.phone}</TeacherDetails>
                </TeacherCard>
              ))}
            </TeachersGrid>
          </>
        ) : (
          <AccessDeniedMessage>
            <h3>Access Denied</h3>
            <p>You don't have permission to view teachers. Please contact an administrator.</p>
          </AccessDeniedMessage>
        )}
      </Container>
    </TeachersContainer>
  );
};

export default Teachers;
