import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';

const SidebarContainer = styled.div`
  width: 250px;
  background: ${props => props.theme.background};
  border-right: 1px solid ${props => props.theme.border};
  padding: 20px 0;
  height: calc(100vh - 70px);
  position: sticky;
  top: 70px;
  overflow-y: auto;
`;

const MenuItem = styled(Link)`
  display: flex;
  align-items: center;
  padding: 12px 20px;
  margin: 5px 10px;
  border-radius: 8px;
  color: ${props => props.theme.text};
  text-decoration: none;
  transition: all 0.3s ease;
  font-weight: 500;

  &:hover {
    background: ${props => props.theme.primary}20;
    color: ${props => props.theme.primary};
    transform: translateX(5px);
  }

  &.active {
    background: ${props => props.theme.primary};
    color: white;
  }
`;

const Icon = styled.span`
  margin-right: 12px;
  font-size: 20px;
`;

const AdminBadge = styled.span`
  background: #ef4444;
  color: white;
  padding: 2px 8px;
  border-radius: 12px;
  font-size: 10px;
  margin-left: auto;
`;

const Sidebar = ({ user }) => {
  const location = useLocation();

  const adminMenuItems = [
    { path: '/', label: 'Dashboard', icon: 'üìä' },
    { path: '/teachers', label: 'Teachers', icon: 'üë®‚Äçüè´' },
    { path: '/performance', label: 'Performance', icon: 'üìà' },
    { path: '/feedback', label: 'Feedback', icon: 'üí¨' },
    { path: '/training', label: 'Training', icon: 'üéì' },
    { path: '/milestones', label: 'Milestones', icon: 'üèÜ' },
    { path: '/profile', label: 'Profile', icon: 'üë§' },
  ];

  const isAdmin = user && user.role === 'admin';

  if (!isAdmin) {
    return (
      <SidebarContainer>
        <MenuItem
          to="/"
          className={location.pathname === '/' ? 'active' : ''}
        >
          <Icon>üìä</Icon>
          <span>Dashboard</span>
        </MenuItem>
        <MenuItem
          to="/profile"
          className={location.pathname === '/profile' ? 'active' : ''}
        >
          <Icon>üë§</Icon>
          <span>Profile</span>
        </MenuItem>
      </SidebarContainer>
    );
  }

  return (
    <SidebarContainer>
      {adminMenuItems.map(item => (
        <MenuItem
          key={item.path}
          to={item.path}
          className={location.pathname === item.path ? 'active' : ''}
        >
          <Icon>{item.icon}</Icon>
          <span>{item.label}</span>
          {item.path !== '/' && item.path !== '/profile' && (
            <AdminBadge>ADMIN</AdminBadge>
          )}
        </MenuItem>
      ))}
    </SidebarContainer>
  );
};

export default Sidebar;
