import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { Container } from '../styles/globalStyles';

const LoginContainer = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
`;

const AuthCard = styled.div`
  background: white;
  border-radius: 16px;
  padding: 40px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
  width: 100%;
  max-width: 450px;
`;

const AuthTitle = styled.h1`
  text-align: center;
  color: #6200ee;
  margin-bottom: 30px;
  font-size: 28px;
  font-weight: 700;
`;

const TabContainer = styled.div`
  display: flex;
  margin-bottom: 30px;
  border-bottom: 2px solid #f0f0f0;
`;

const Tab = styled.button`
  flex: 1;
  padding: 15px;
  background: none;
  border: none;
  font-size: 16px;
  font-weight: 600;
  color: ${props => props.active ? '#6200ee' : '#666'};
  cursor: pointer;
  border-bottom: 3px solid ${props => props.active ? '#6200ee' : 'transparent'};
  transition: all 0.3s ease;
`;

const AuthForm = styled.form`
  display: flex;
  flex-direction: column;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
`;

const FormLabel = styled.label`
  display: block;
  margin-bottom: 8px;
  font-weight: 500;
  color: #333;
`;

const FormInput = styled.input`
  width: 100%;
  padding: 12px;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  background: #fff;
  color: #333;
  font-size: 16px;

  &:focus {
    outline: none;
    border-color: #6200ee;
    box-shadow: 0 0 0 2px rgba(98, 0, 238, 0.2);
  }
`;

const AuthButton = styled.button`
  background: linear-gradient(45deg, #6200ee, #ff4081);
  color: white;
  border: none;
  border-radius: 8px;
  padding: 14px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 10px;

  &:hover {
    opacity: 0.9;
    transform: translateY(-2px);
    box-shadow: 0 8px 20px rgba(98, 0, 238, 0.4);
  }
`;

const ErrorMessage = styled.div`
  background: #fee;
  color: #c53030;
  padding: 12px;
  border-radius: 8px;
  border: 1px solid #feb2b2;
  margin-bottom: 20px;
  text-align: center;
`;

const SuccessMessage = styled.div`
  background: #f0fff4;
  color: #2f855a;
  padding: 12px;
  border-radius: 8px;
  border: 1px solid #9ae6b4;
  margin-bottom: 20px;
  text-align: center;
`;

const AdminCredentials = styled.div`
  margin-top: 20px;
  padding: 15px;
  background: #f7f7f7;
  border-radius: 8px;
  border: 1px solid #e0e0e0;
`;

const CredentialTitle = styled.h4`
  margin-bottom: 10px;
  color: #333;
`;

const CredentialItem = styled.div`
  margin-bottom: 5px;
  font-size: 14px;
  color: #666;
`;

const Login = ({ onLogin }) => {
  const [activeTab, setActiveTab] = useState('login');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password
        })
      });

      const data = await response.json();

      if (response.ok) {
        onLogin(data.user, data.token);
        navigate('/');
      } else {
        setError(data.message || 'Login failed');
      }
    } catch (error) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');

    // Validation
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          password: formData.password
        })
      });

      const data = await response.json();

      if (response.ok) {
        setSuccess('Registration successful! You can now login.');
        setActiveTab('login');
        setFormData({
          name: '',
          email: '',
          password: '',
          confirmPassword: ''
        });
      } else {
        setError(data.message || 'Registration failed');
      }
    } catch (error) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const fillAdminCredentials = () => {
    setFormData({
      ...formData,
      email: 'admin@edutrack.com',
      password: 'admin123'
    });
    setError('');
  };

  const fillNisalCredentials = () => {
    setFormData({
      ...formData,
      email: 'nisal@edutrack.com',
      password: 'nisal123'
    });
    setError('');
  };

  return (
    <LoginContainer>
      <Container>
        <AuthCard>
          <AuthTitle>EduTrack</AuthTitle>

          <TabContainer>
            <Tab 
              active={activeTab === 'login'} 
              onClick={() => setActiveTab('login')}
            >
              Login
            </Tab>
            <Tab 
              active={activeTab === 'register'} 
              onClick={() => setActiveTab('register')}
            >
              Sign Up
            </Tab>
          </TabContainer>

          {error && <ErrorMessage>{error}</ErrorMessage>}
          {success && <SuccessMessage>{success}</SuccessMessage>}

          {activeTab === 'login' ? (
            <AuthForm onSubmit={handleLogin}>
              <FormGroup>
                <FormLabel>Email</FormLabel>
                <FormInput
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="Enter your email"
                  required
                />
              </FormGroup>

              <FormGroup>
                <FormLabel>Password</FormLabel>
                <FormInput
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Enter your password"
                  required
                />
              </FormGroup>

              <AuthButton type="submit" disabled={loading}>
                {loading ? 'Signing in...' : 'Sign In'}
              </AuthButton>
            </AuthForm>
          ) : (
            <AuthForm onSubmit={handleRegister}>
              <FormGroup>
                <FormLabel>Full Name</FormLabel>
                <FormInput
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="Enter your full name"
                  required
                />
              </FormGroup>

              <FormGroup>
                <FormLabel>Email</FormLabel>
                <FormInput
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="Enter your email"
                  required
                />
              </FormGroup>

              <FormGroup>
                <FormLabel>Password</FormLabel>
                <FormInput
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Create a password (min. 6 characters)"
                  required
                />
              </FormGroup>

              <FormGroup>
                <FormLabel>Confirm Password</FormLabel>
                <FormInput
                  type="password"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  placeholder="Confirm your password"
                  required
                />
              </FormGroup>

              <AuthButton type="submit" disabled={loading}>
                {loading ? 'Creating Account...' : 'Create Account'}
              </AuthButton>
            </AuthForm>
          )}

          <AdminCredentials>
            <CredentialTitle>Admin Demo Credentials:</CredentialTitle>
            <CredentialItem>
              <strong>Admin:</strong> admin@edutrack.com / admin123
            </CredentialItem>
            <CredentialItem>
              <strong>Nisal:</strong> nisal@edutrack.com / nisal123
            </CredentialItem>

            <div style={{ display: 'flex', gap: '10px', marginTop: '15px' }}>
              <button 
                onClick={fillAdminCredentials}
                style={{
                  padding: '8px 12px',
                  border: '1px solid #6200ee',
                  borderRadius: '6px',
                  background: 'transparent',
                  color: '#6200ee',
                  cursor: 'pointer',
                  fontSize: '12px'
                }}
              >
                Use Admin
              </button>
              <button 
                onClick={fillNisalCredentials}
                style={{
                  padding: '8px 12px',
                  border: '1px solid #6200ee',
                  borderRadius: '6px',
                  background: 'transparent',
                  color: '#6200ee',
                  cursor: 'pointer',
                  fontSize: '12px'
                }}
              >
                Use Nisal
              </button>
            </div>
          </AdminCredentials>
        </AuthCard>
      </Container>
    </LoginContainer>
  );
};

export default Login;
