import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Handle response errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth API
export const authAPI = {
  login: (credentials) => api.post('/auth/login', credentials),
  register: (userData) => api.post('/auth/register', userData),
  getProfile: () => api.get('/auth/profile'),
  updateProfile: (userData) => api.put('/auth/profile', userData),
};

// Teachers API
export const teachersAPI = {
  getAll: () => api.get('/teachers'),
  getById: (id) => api.get(`/teachers/${id}`),
  create: (teacherData) => api.post('/teachers', teacherData),
  update: (id, teacherData) => api.put(`/teachers/${id}`, teacherData),
  delete: (id) => api.delete(`/teachers/${id}`),
};

// Feedback API
export const feedbackAPI = {
  getAll: () => api.get('/feedback'),
  getByTeacher: (teacherId) => api.get(`/feedback/teacher/${teacherId}`),
  create: (feedbackData) => api.post('/feedback', feedbackData),
  update: (id, feedbackData) => api.put(`/feedback/${id}`, feedbackData),
  delete: (id) => api.delete(`/feedback/${id}`),
};

// For demo purposes - mock API calls
export const mockAPI = {
  getTeachers: async () => {
    return {
      data: [
        {
          _id: '1',
          name: 'Sarah Johnson',
          subject: 'Mathematics',
          avatar: 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-4.0.3&auto=format&fit=crop&w=200&q=80',
          performance: 92,
          students: 28,
          years: 5
        }
      ]
    };
  }
};

// Enhanced API with fallback
export const apiWithFallback = {
  getTeachers: async () => {
    try {
      return await teachersAPI.getAll();
    } catch (error) {
      console.log('Using mock data for teachers');
      return await mockAPI.getTeachers();
    }
  }
};

export default api;
