  background: #10b981;
  color: white;
  border: none;
  padding: 8px 14px;
  border-radius: 6px;
  cursor: pointer;
`;

const FeedbackList = styled.div`
  display: grid;
  gap: 12px;
`;

const FeedbackItem = styled.div`
  display:flex;
  gap:12px;
  background: #fff;
  padding: 12px;
