  box-shadow: 0 3px 10px rgba(0,0,0,0.04);
`;

const Avatar = styled.img`
  width:56px;
  height:56px;
  border-radius:50%;
  object-fit:cover;
`;

const FeedbackContent = styled.div`
  flex:1;
`;

const FeedbackHeader = styled.div`
  display:flex;
  justify-content:space-between;
  align-items:center;
  margin-bottom:6px;
`;

const FeedbackMeta = styled.div`
  font-size:0.85rem;
  color:#6b7280;
`;

const NoFeedback = styled.div`
  padding: 16px; color:#6b7280; text-align:center;
`;

// Component
const Feedback = ({ user }) => {
  const [showForm, setShowForm] = useState(false);
  const [feedbacks, setFeedbacks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [newFeedback, setNewFeedback] = useState({
    student: '',
    course: '',
    rating: 0,
    text: '',
    teacherId: '654d4b5e8a1b2c3d4e5f6a7b' // Default teacher ID
  });
  const [tempRating, setTempRating] = useState(0);

  const teachers = [
    { id: '654d4b5e8a1b2c3d4e5f6a7b', name: 'Sarah Johnson', subject: 'Mathematics' },
    { id: '654d4b5e8a1b2c3d4e5f6a7c', name: 'Michael Chen', subject: 'Science' },
    { id: '654d4b5e8a1b2c3d4e5f6a7d', name: 'Emily Rodriguez', subject: 'English' }
  ];

  const courses = [
    'Mathematics 101',
    'Science Fundamentals',
    'English Literature',
    'World History',
    'Art Appreciation'
  ];

  useEffect(() => {
    let mounted = true;
    const load = async () => {
      try {
        setLoading(true);
        const response = await feedbackAPI.getAll();
        if (!mounted) return;
        setFeedbacks(response?.data || []);
      } catch (err) {
        console.error('Error loading feedbacks:', err);
        setError('Failed to load feedbacks. Showing demo data.');
        // fallback demo data
        setFeedbacks([
          {
            _id: '1',
            student: 'Emma Thompson',
            avatar: 'https://images.unsplash.com/photo-1544725176-7c40e5a71c5e?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&q=80',
            course: 'Mathematics 101',
            rating: 5,
            text: "Professor Johnson explains complex concepts in a way that's easy to understand.",
            date: '2023-10-15'
          },
          {
            _id: '2',
            student: 'James Wilson',
            avatar: 'https://images.unsplash.com/photo-1531427186611-ecfd6d936c79?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&q=80',
            course: 'Science Fundamentals',
            rating: 4,
            text: 'Mr. Chen makes science exciting with his hands-on experiments.',
            date: '2023-10-12'
          }
        ]);
      } finally {
        if (mounted) setLoading(false);
      }
    };

    load();
    return () => { mounted = false; };
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewFeedback(prev => ({
      ...prev,
      [name]: name === 'rating' ? parseInt(value || '0', 10) : value
    }));
  };

  const handleRatingClick = (rating) => {
    setNewFeedback(prev => ({ ...prev, rating }));
    setTempRating(0);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newFeedback.student || !newFeedback.course || !newFeedback.text) {
      setError('Please fill all required fields.');
      return;
    }

    try {
      setError('');
      const response = await feedbackAPI.create(newFeedback);

      // Use returned data when possible, otherwise build one for UI
      const created = response?.data || {
        _id: String(Date.now()),
        ...newFeedback,
        avatar: '',
        date: new Date().toISOString()
      };

      setFeedbacks(prev => [created, ...prev]);

      setNewFeedback({
        student: '',
        course: '',
        rating: 0,
        text: '',
        teacherId: '654d4b5e8a1b2c3d4e5f6a7b'
      });

      setShowForm(false);
    } catch (err) {
      console.error('Error creating feedback:', err);
      setError('Failed to save feedback. Please try again.');
    }
  };

  const handleCancel = () => {
    setNewFeedback({
      student: '',
      course: '',
      rating: 0,
      text: '',
      teacherId: '654d4b5e8a1b2c3d4e5f6a7b'
    });
    setShowForm(false);
    setError('');
  };

  const renderStars = (rating) => (
    [...Array(5)].map((_, i) => (
      <span key={i}>{i < rating ? '★' : '☆'}</span>
    ))
  );

  const formatDate = (dateString) => {
    if (!dateString) return '';
    try {
      return new Date(dateString).toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric' });
    } catch (err) {
      return dateString;
    }
  };

  if (loading) {
    return (
      <FeedbackContainer>
        <Container>
          <SectionTitle>Student Feedback</SectionTitle>
          <div style={{ textAlign: 'center', padding: '40px' }}>
            <p>Loading feedbacks...</p>
          </div>
        </Container>
      </FeedbackContainer>
    );
  }

  return (
    <FeedbackContainer>
      <Container>
        <SectionTitle>Student Feedback</SectionTitle>

        {error && (
          <div style={{ background: '#fee', color: '#c53030', padding: '12px', borderRadius: '8px', marginBottom: '20px', border: '1px solid #feb2b2' }}>
            {error}
          </div>
        )}

        {user?.role === 'admin' && (
          <AddFeedbackButton onClick={() => setShowForm(s => !s)}>
            {showForm ? 'Cancel' : '+ Add New Feedback'}
          </AddFeedbackButton>
        )}

        {showForm && user?.role === 'admin' && (
          <FeedbackForm>
            <FormTitle>Add New Feedback</FormTitle>
            <form onSubmit={handleSubmit}>
              <FormGrid>
                <FormGroup>
                  <FormLabel htmlFor="student">Student Name</FormLabel>
                  <FormInput id="student" name="student" type="text" value={newFeedback.student} onChange={handleInputChange} placeholder="Enter student name" required />
                </FormGroup>

                <FormGroup>
                  <FormLabel htmlFor="course">Course</FormLabel>
                  <FormSelect id="course" name="course" value={newFeedback.course} onChange={handleInputChange} required>
                    <option value="">Select a course</option>
                    {courses.map((course, index) => (
                      <option key={index} value={course}>{course}</option>
                    ))}
                  </FormSelect>
                </FormGroup>

                <FormGroup>
                  <FormLabel htmlFor="teacherId">Teacher</FormLabel>
                  <FormSelect id="teacherId" name="teacherId" value={newFeedback.teacherId} onChange={handleInputChange} required>
                    <option value="">Select a teacher</option>
                    {teachers.map((teacher) => (
                      <option key={teacher.id} value={teacher.id}>{teacher.name} - {teacher.subject}</option>
                    ))}
                  </FormSelect>
                </FormGroup>
              </FormGrid>

              <FormGroup>
                <FormLabel>Rating</FormLabel>
                <RatingContainer>
                  {[1,2,3,4,5].map(star => (
                    <RatingStar
                      key={star}
                      type="button"
                      active={star <= (tempRating || newFeedback.rating)}
                      onClick={() => handleRatingClick(star)}
                      onMouseEnter={() => setTempRating(star)}
                      onMouseLeave={() => setTempRating(0)}
                      aria-label={`Rate ${star} star${star>1?'s':''}`}
                    >
                      {star <= (tempRating || newFeedback.rating) ? '★' : '☆'}
                    </RatingStar>
                  ))}
                </RatingContainer>
                <div style={{ fontSize: '14px', color: '#666', marginTop: 6 }}>
                  {newFeedback.rating === 0 ? 'No rating' : `${newFeedback.rating} out of 5 stars`}
                </div>
              </FormGroup>

              <FormGroup>
                <FormLabel htmlFor="text">Feedback</FormLabel>
                <FormTextarea id="text" name="text" value={newFeedback.text} onChange={handleInputChange} placeholder="Enter your feedback here..." required />
              </FormGroup>

              <FormActions>
                <CancelButton type="button" onClick={handleCancel}>Cancel</CancelButton>
                <SubmitButton type="submit">Submit</SubmitButton>
              </FormActions>
            </form>
          </FeedbackForm>
        )}

        <FeedbackList>
          {feedbacks.length === 0 ? (
            <NoFeedback>No feedbacks yet.</NoFeedback>
          ) : (
            feedbacks.map(fb => (
              <FeedbackItem key={fb._id || fb.id}>
                <div>
                  <Avatar src={fb.avatar || 'https://via.placeholder.com/100'} alt={`${fb.student || 'Student'} avatar`} />
                </div>
                <FeedbackContent>
                  <FeedbackHeader>
                    <div>
                      <div style={{ fontWeight: 700 }}>{fb.student || 'Unknown Student'}</div>
                      <FeedbackMeta>{fb.course || ''} {fb.teacher && `· ${fb.teacher}`}</FeedbackMeta>
                    </div>
                    <div style={{ textAlign: 'right' }}>
                      <div>{renderStars(fb.rating || 0)}</div>
                      <div style={{ fontSize: '12px', color: '#6b7280' }}>{formatDate(fb.date)}</div>
                    </div>
                  </FeedbackHeader>
                  <div>{fb.text}</div>
                </FeedbackContent>
              </FeedbackItem>
            ))
          )}
        </FeedbackList>

      </Container>
    </FeedbackContainer>
  );
};

export default Feedback;
